#include <iostream>   using namespace std;    void heapify(int arr[], int n, int i) {     int largest = i;      int l = 2 * i + 1;     int r = 2 * i + 2;            if (l < n && arr[l] > arr[largest])         largest = l;            if (r < n && arr[r] > arr[largest])         largest = r;            if (largest != i) {         swap(arr[i], arr[largest]);                heapify(arr, n, largest);     } }    void heapSort(int arr[], int n) {      for (int i = n / 2 - 1; i >= 0; i--)         heapify(arr, n, i);        for (int i = n - 1; i > 0; i--) {                 swap(arr[0], arr[i]);         heapify(arr, i, 0);     } }    void printArray(int arr[], int n) {     for (int i = 0; i < n; ++i)         cout << arr[i] << " ";     cout << "
"; }    int main() {     int arr[] = { 12, 11, 13, 5, 6, 7 };     int n = sizeof(arr) / sizeof(arr[0]);       heapSort(arr, n);       cout << "Sorted array is 
";     printArray(arr, n); }
Implementation of heap sort in C:  #include <stdio.h> int main() {    int heap[10], array_size, i, j, c, root, temporary;    printf("
 Enter size of array to be sorted :");    scanf("%d", &array_size);    printf("
 Enter the elements of array : ");    for (i = 0; i < array_size; i++)       scanf("%d", &heap[i]);    for (i = 1; i < array_size; i++)    {        c = i;        do        {            root = (c - 1) / 2;                        if (heap[root] < heap[c])   /* to create MAX heap array */            {                                  // if child is greater than parent swap them                temporary = heap[root];      // as structure is of complete binary tree                heap[root] = heap[c];     // it took logn steps to reach from root to leaf                heap[c] = temporary;            }            c = root;        } while (c != 0);    }    printf("Heap array : ");    for (i = 0; i < array_size; i++)        printf("%d	 ", heap[i]);         //printing the heap array    for (j = array_size - 1; j >= 0; j--)    {        temporary = heap[0];        heap[0] = heap[j] ;   /* swap max element with rightmost leaf element */        heap[j] = temporary;        root = 0;        do        {            c = 2 * root + 1;    /* left node of root element */            if ((heap[c] < heap[c + 1]) && c < j-1)                c++;            if (heap[root]<heap[c] && c<j)    /* again rearrange to max heap array */            {                temporary = heap[root];                heap[root] = heap[c];                heap[c] = temporary;            }            root = c;        } while (c < j);    }    printf("
 The sorted array is : ");    for (i = 0; i < array_size; i++)       printf("	 %d", heap[i]); }

int height(Node* root) {     // Base case: empty tree has height 0     if (root == nullptr)         return 0;       // recur for left and right subtree and consider maximum depth     return 1 + max(height(root->left), height(root->right)); } 
// finding height of a binary tree in c++. int maxDepth(node* node)   {       if (node == NULL)           return 0;       else     {           /* compute the depth of each subtree */         int lDepth = maxDepth(node->left);           int rDepth = maxDepth(node->right);                  /* use the larger one */         if (lDepth > rDepth)               return(lDepth + 1);           else return(rDepth + 1);       }   }  
# define a Class Tree, to intiate the binary tree class TreeNode:     def __init__(self, val):         self.val = val         self.left = None         self.right = None   def height(root):       # Check if the binary tree is empty     if root is None:         # If TRUE return 0         return 0      # Recursively call height of each node     leftAns = height(root.left)     rightAns = height(root.right)       # Return max(leftHeight, rightHeight) at each iteration     return max(leftAns, rightAns) + 1   # Test the algorithm root = TreeNode(1) root.left = TreeNode(2) root.right = TreeNode(3) root.left.left = TreeNode(4)   print("Height of the binary tree is: " + str(height(root))) 
/*C++ Program for finding the height of the Binary tree */#include<iostream>using namespace std;/* A binary tree node has data, pointer to left child and a pointer to right child */struct Node{	int data;	Node* left;	Node* right;	/*For initilaizing the Node*/	Node(int val)	{		data=val;		left=NULL;		right=NULL;	}};//Recursive function for finding the Height of the Binary Treeint height(Node* rt){	int lheight,rheight;	if(rt==NULL) //If rt is NULL return 0		return 0;	else	{		lheight = height(rt->left); //Left Subtree		rheight = height(rt->right); //Right Subtree		if(lheight>rheight) //checking which is maximum (lheight or rheight) 			return lheight+1;		else			return rheight+1;	}}/* DRIVER FUNCTION */int main(){	/*Insert the Elements according to the Tree (Picture)*/	Node* root = new Node(1);	root->left = new Node(2);     root->right = new Node(3);     root->left->left = new Node(4);     root->left->right = new Node(5);    cout<<"Height of the Binary Tree is "<<height(root);	return 0;}

