// @see https://www.youtube.com/watch?v=es2T6KY45cA&vl=en // @see https://www.cs.usfca.edu/~galles/visualization/ComparisonSort.html  function merge(list, start, midpoint, end) {     const left = list.slice(start, midpoint);     const right = list.slice(midpoint, end);     for (let topLeft = 0, topRight = 0, i = start; i < end; i += 1) {         if (topLeft >= left.length) {             list[i] = right[topRight++];         } else if (topRight >= right.length) {             list[i] = left[topLeft++];         } else if (left[topLeft] < right[topRight]) {             list[i] = left[topLeft++];         } else {             list[i] = right[topRight++];         }     } }  function mergesort(list, start = 0, end = undefined) {     if (end === undefined) {         end = list.length;     }     if (end - start > 1) {         const midpoint = ((end + start) / 2) >> 0;         mergesort(list, start, midpoint);         mergesort(list, midpoint, end);         merge(list, start, midpoint, end);     }     return list; }  mergesort([4, 7, 2, 6, 4, 1, 8, 3]);
#include<iostream> using namespace std; void swapping(int &a, int &b) {     //swap the content of a and b    int temp;    temp = a;    a = b;    b = temp; } void display(int *array, int size) {    for(int i = 0; i<size; i++)       cout << array[i] << " ";    cout << endl; } void merge(int *array, int l, int m, int r) {    int i, j, k, nl, nr;    //size of left and right sub-arrays    nl = m-l+1; nr = r-m;    int larr[nl], rarr[nr];    //fill left and right sub-arrays    for(i = 0; i<nl; i++)       larr[i] = array[l+i];    for(j = 0; j<nr; j++)       rarr[j] = array[m+1+j];    i = 0; j = 0; k = l;    //marge temp arrays to real array    while(i < nl && j<nr) {       if(larr[i] <= rarr[j]) {          array[k] = larr[i];          i++;       }else{          array[k] = rarr[j];          j++;       }       k++;    }    while(i<nl) {       //extra element in left array       array[k] = larr[i];       i++; k++;    }    while(j<nr) {     //extra element in right array       array[k] = rarr[j];       j++; k++;    } } void mergeSort(int *array, int l, int r) {    int m;    if(l < r) {       int m = l+(r-l)/2;       // Sort first and second arrays       mergeSort(array, l, m);       mergeSort(array, m+1, r);       merge(array, l, m, r);    } } int main() {    int n;    cout << "Enter the number of elements: ";    cin >> n;    int arr[n];     //create an array with given number of elements    cout << "Enter elements:" << endl;    for(int i = 0; i<n; i++) {       cin >> arr[i];    }    cout << "Array before Sorting: ";    display(arr, n);    mergeSort(arr, 0, n-1);     //(n-1) for last index    cout << "Array after Sorting: ";    display(arr, n); }
/*       a[] is the array, p is starting index, that is 0,      and r is the last index of array.  */  #include <stdio.h>  // lets take a[5] = {32, 45, 67, 2, 7} as the array to be sorted.  // merge sort function void mergeSort(int a[], int p, int r) {     int q;     if(p < r)     {         q = (p + r) / 2;         mergeSort(a, p, q);         mergeSort(a, q+1, r);         merge(a, p, q, r);     } }  // function to merge the subarrays void merge(int a[], int p, int q, int r) {     int b[5];   //same size of a[]     int i, j, k;     k = 0;     i = p;     j = q + 1;     while(i <= q && j <= r)     {         if(a[i] < a[j])         {             b[k++] = a[i++];    // same as b[k]=a[i]; k++; i++;         }         else         {             b[k++] = a[j++];         }     }        while(i <= q)     {         b[k++] = a[i++];     }        while(j <= r)     {         b[k++] = a[j++];     }        for(i=r; i >= p; i--)     {         a[i] = b[--k];  // copying back the sorted list to a[]     }  }  // function to print the array void printArray(int a[], int size) {     int i;     for (i=0; i < size; i++)     {         printf("%d ", a[i]);     }     printf("
"); }   int main() {     int arr[] = {32, 45, 67, 2, 7};     int len = sizeof(arr)/sizeof(arr[0]);       printf("Given array: 
");     printArray(arr, len);          // calling merge sort     mergeSort(arr, 0, len - 1);       printf("
Sorted array: 
");     printArray(arr, len);     return 0; }
// Merge two subarrays L and M into arr void merge(int arr[], int p, int q, int r) {      // Create L &larr; A[p..q] and M &larr; A[q+1..r]     int n1 = q - p + 1;     int n2 = r - q;      int L[n1], M[n2];      for (int i = 0; i < n1; i++)         L[i] = arr[p + i];     for (int j = 0; j < n2; j++)         M[j] = arr[q + 1 + j];      // Maintain current index of sub-arrays and main array     int i, j, k;     i = 0;     j = 0;     k = p;      // Until we reach either end of either L or M, pick larger among     // elements L and M and place them in the correct position at A[p..r]     while (i < n1 && j < n2) {         if (L[i] <= M[j]) {             arr[k] = L[i];             i++;         } else {             arr[k] = M[j];             j++;         }         k++;     }      // When we run out of elements in either L or M,     // pick up the remaining elements and put in A[p..r]     while (i < n1) {         arr[k] = L[i];         i++;         k++;     }      while (j < n2) {         arr[k] = M[j];         j++;         k++;     } }
#include "tools.hpp" /*   >>>>>>>> (Recursive function that sorts a sequence of) <<<<<<<<<<<<       >>>>>>>> (numbers in ascending order using the merge function) <<<<                                 */ std::vector<int> sort(size_t start, size_t length, const std::vector<int>& vec) { 	if(vec.size()==0 ||vec.size() == 1) 	return vec;  	vector<int> left,right; //===>  creating left and right vectors   	size_t mid_point = vec.size()/2; //===>   midle point between the left vector and the right vector   	for(int i = 0 ; i < mid_point; ++i){left.emplace_back(vec[i]);} //===>  left vector  	for(int j = mid_point; j < length; ++j){ right.emplace_back(vec[j]);} //===>  right vector   	left = sort(start,mid_point,left); //===>  sorting the left vector  	right = sort(mid_point,length-mid_point,right);//===>  sorting the right vector  	  	return merge(left,right); //===>   all the function merge to merge between the left and the right } /*  >>>>> (function that merges two sorted vectors of numberss) <<<<<<<<<                                    */  vector<int> merge(const vector<int>& a, const vector<int>& b) { 	vector<int> merged_a_b(a.size()+b.size(),0); // temp vector that includes both left and right vectors 	int i = 0; 	int j = 0; 	int k = 0; 	int left_size = a.size(); 	int right_size = b.size(); 	while(i<left_size && j<right_size)  	{ 		if(a[i]<b[j]) 		{ 			merged_a_b[k]=a[i]; 			i++; 		} 		else 		{ 			merged_a_b[k]=b[j]; 			j++; 		} 		k++; 	} 	while(i<left_size) 	{ 		merged_a_b[k]=a[i]; 		i++; 		k++; 	} 	while(j<right_size) 	{ 		merged_a_b[k]=b[j]; 		j++; 		k++; 	} 	 	return merged_a_b;  }

