//bigint.cpp methods implementation #include <stdexcept> #include"Bigint.hpp" #include<algorithm> /** //**************************** overloading operator multiplication ******************************************** ********************************         Amir Ammar                 *******************************************/ Bigint operator*(const Bigint& a, const Bigint& b){   Bigint temp1 = a;   Bigint temp2 = b;   Bigint temp3;  // using temp3 object as a temporary to store the chars each iteration    temp3.m_digits ={'0'};   Bigint temp4;  // for the arithmetic operation + between (temp3+temp4) and next temp4 to store the value of temp3    temp4.m_digits = {'0'};   Bigint mult;      // the final values will be stored here and we return mult    mult.m_digits = {'0'};   if(temp1.is_zero()||temp2.is_zero()){return mult.m_digits;}  // if one of them is zero we return mult == '0';   char devide_by_ten{'0'};     // initializing devided by ten variable as a char type   char devide_by_modulo{'0'};  // initializing devided by modulo variable as a char type    int size_of_in {0};          // size of in is a variable that will decide next how many zeroes will be pushed inside temp3 each iteration   if(temp1.m_digits>temp2.m_digits){  // if statement to in     size_of_in = temp2.m_digits.size();// i decided to intilize it according to the size of the bigger Bigint.size()   }else{     size_of_in = temp1.m_digits.size();   }   int push_zeros = size_of_in ; // we need to loop and push zeroes so i need this variable first to have the same value as size_of_in   int counter = 0; // to increment the counter each time a zero is pushed and finally to break the loop    int minus_one = 0; // to decrment the valuee by one each loop   auto iterator1=temp1.m_digits.end();    auto iterator2=temp2.m_digits.end();   // auto iterator1_1=temp1.m_digits.end(); // those variables was a part of experiment that succeeded    // auto iterator2_2=temp2.m_digits.end();  // those variables was a part of experiment that succeeded     while(true)//iterator1_1!=temp1.begin()||iterator2_2!=temp2.begin()   {       temp3.m_digits.clear();                              // cleaning the temp3 each loop so we can store the new value          while(push_zeros+minus_one+counter != size_of_in){ // while loop to push zero for temp3 each iteration           ++counter;           temp3.m_digits.emplace_front('0');         }         --minus_one;         counter =0;         --iterator2;          if(iterator2==temp2.m_digits.end())break;  // break when we finally reached the last iteration              while(iterator1!=temp1.m_digits.begin()){                --iterator1;               devide_by_modulo=((*iterator1-48)*(*iterator2-48)+(devide_by_ten-48))+'0';               devide_by_ten = ((*iterator1-'0')*(*iterator2-'0')+(devide_by_ten-'0'))/10+'0';               temp3.m_digits.emplace_front(devide_by_modulo);             }             if(devide_by_ten != '0')temp3.m_digits.emplace_front(devide_by_ten);             devide_by_modulo = {'0'};             devide_by_ten ={'0'};     mult = temp3+temp4; // the arithmetic operation between temp3+temp4 stored inside mult      temp4= temp4+temp3;  // the result will be also stored inside temp4 so we can do another + operation but with different values inside temp3     iterator1=temp1.m_digits.end();// initializing the iterator1 as the end so we could loop again with same values      // --iterator2_2; // experiment      // --iterator1_1; // experiment    }   if(a.m_is_negative==true&& b.m_is_negative==true){return mult;} // if both negative we return mult with none negative sign    if(a.m_is_negative==true||b.m_is_negative==true)mult.m_digits.emplace_front('-'); // if one of them is negative we push -    return mult;  // and finally we return mult  }
