 }   if(temp1.is_negative() && !temp2.is_negative()){  // (-temp1) - (+temp2)       temp2.m_is_negative = true;      return temp1+temp2;       }   if(temp1.is_negative()&&temp2.is_negative()){     temp1.m_is_negative = false;     temp2.m_is_negative = false;     return temp2 - temp1 ;   }   auto it_1 = temp1.m_digits.end();    auto it_2 = temp2.m_digits.end();    int one_less{0};   int length = temp1.m_digits.size()-temp2.m_digits.size();   if(length <0){     for(int i = length ; i<0; ++i) //      ((temp1.m_digits.size()>temp2.m_digits.size())?temp2.m_digits.emplace_front('0'):temp1.m_digits.emplace_front('0'));   }else   {     for(int i = length; i>0; --i)       ((temp1.m_digits.size()>temp2.m_digits.size())?temp2.m_digits.emplace_front('0'):temp1.m_digits.emplace_front('0'));   }   auto it1 = temp1.m_digits.end(); auto it2 = temp2.m_digits.end();   while(it1 != temp1.m_digits.begin() && it2 != temp2.m_digits.begin()){       --it1; --it2;        int it1_int = *it1H;       int it2_int = *it2H;     if(it1_int-it2_int-one_less<0){       it1_int+= 10;       sub.m_digits.emplace_front(it1_int-it2_int-one_less+'0');       one_less = 1;     }else{       sub.m_digits.emplace_front(it1_int-it2_int-one_less+'0');       one_less = 0;     }   }   if(one_less==1){     sub=temp2-temp1 ;     sub.m_digits.emplace_front('-');   }   while(sub.m_digits.front()=='0'&&sub.m_digits.size()!= 1){     sub.m_digits.pop_front();     if(sub.m_digits.size()== 1)       break;   }   return sub.m_digits; }
