#include <stdio.h> #include <math.h> #define COLUMN 6  void createPattern(char pattern[][COLUMN],int numStudent,int row) {     for(int i = 0; i < row ; i++) {         for(int j = 0; j < COLUMN; j++) {             if(numStudent!=0) {                 pattern[i][j] = 'X';                 numStudent -- ;             }             else if(numStudent==0) {                 pattern[i][j] = ' ';             }         }     } }  void bookSeat(char pattern[][COLUMN],int rowB,int columnB) {     if(pattern[rowB-1][columnB-1] != ' ') {         pattern[rowB-1][columnB-1] = 'S';     } }  void printPattern(char pattern[][COLUMN],int row) {     for(int i = 0; i < row ; i++) {         for(int j = 0; j < COLUMN; j++) {             printf("%c",pattern[i][j]);             if(j < COLUMN-1) {                 printf(" ");             }         }         printf("\n");     } } int main() {     int numStudent,numSeat;     scanf("%d",&numStudent);     if(numStudent>=1 && numStudent<=40) {         int rowBook,columnBook;         int row = ceil((double)numStudent/COLUMN);         char pattern[row][COLUMN];         createPattern(pattern,numStudent,row);          scanf("%d",&numSeat);          for(int i = 0 ; i < numSeat ; i++) {             scanf("%d %d",&rowBook,&columnBook);             if((rowBook >= 1 && rowBook <= row ) && (columnBook >= 1 && columnBook <= COLUMN)) {                 bookSeat(pattern,rowBook,columnBook);             }         }         printPattern(pattern,row);      }     else if(numStudent<1) {         printf("Students not enough.\n");     }     else if(numStudent>40) {         printf("Too many students.\n");     }     return 0; }
#include <bits/stdc++.h> using namespace std;   // Function to check if elements are // pairwise consecutive in queue bool pairWiseConsecutive(queue<int> q) {     // Transfer elements of q to aux.     stack<int> aux;     while (!q.empty()) {         aux.push(q.front());         q.pop();     }       // Again transfer the     // elements of aux to aux2     stack<int> aux2;     while (!aux.empty()) {         aux2.push(aux.top());         aux.pop();     }       // Traverse aux2 and see if     // elements are pairwise     // consecutive or not. We also     // need to make sure that original     // content is retained.     bool result = true;     while (aux2.size() > 1) {           // Fetch current top two         // elements of aux2 and check         // if they are consecutive.         int x = aux2.top();         aux2.pop();           int y = aux2.top();         aux2.pop();           if (abs(x - y) != 1)             result = false;           // Push the elements to queue         q.push(x);         q.push(y);     }       if (aux2.size() == 1)         q.push(aux2.top());       return result; }   // Driver program int main() {     // Pushing elements into the queue     queue<int> q;     q.push(4);     q.push(5);     q.push(-2);     q.push(-3);     q.push(11);     q.push(10);     q.push(5);     q.push(6);       if (pairWiseConsecutive(q))         cout << "Yes" << endl;     else         cout << "No" << endl;       // Printing the original queue     while (!q.empty()) {         cout << q.front() << " ";         q.pop();     }     cout << endl;       return 0; }
