elper.empty())   {     src=helper.front();     for(vector<int>::iterator it=adjacency_list[src].begin();it!+adjacency_list[src].end();it++)     {       if(!visited[it->first])       {         visited[it->first]=true;         cout<<it->first<<" ";         helper.push(it->first);       }     }     helper.pop();   } }  int WeightedGraph::minDistance(vector<int>dist,bool visited[])   {     int min=INT_MAX;     int minIndex=INT_MAX;     for(int i=0;i<N;i++)     {       if(!visited[i]&&dist[i]<=min)       {         min=dist[i];         minIndex=i;       }     }     return minIndex;   }   void WeightedGraph::djikstra(int src)   {     vector<int>dist;     bool visited[vertices];     for(int i=0;i<vertices;i++)     {       dist.push_back(INT_MAX);       visited[i]=false;     }     visited[src]=true;     dist[src]=0;     for(int i=0;i<vertices-1;i++)     {       int k=minDistance(dist,visited);       visited[k]=true;       for(int j=0;j<vertices;j++)       {         if(!visited[i]&&dist[i]!=INT_MAX&&adjacency_list[i][j].second+dist[i]<dist[j])         {           dist[j]=adjacency_list[i][j].second+dist[i];         }       }     }     for(int i=0;i<dist.size();i++)       cout<<dist[i]<<" ";          cout<<endl;   } void WeightedGraph::display_graph() {   int a,b;   //first loop to traverse across vertices   for(int i=0;i<vertices;i++)   {       cout<<"Adjacency list of vertex "<<i<<endl;     //second loop to traverse across the adjacency list of some vertex i     for(auto it=adjacency_list[i].begin();it!=adjacency_list[i].end();it++)     {       //set a as the vertex number and b as the weight       a=it->first;       b=it->second;       cout<<"Vertex : "<<a<<" Weight : "<<b<<endl;     }     cout<<endl;   } }  //thank you!

