#include<iostream> #include<string>  using namespace std; int main() {    string text = "ABCDEFGHIJKLMNOPQRSTUVWXYZ";     cout << "Initial string: " << text << endl;     text.erase(10, string::npos);   // string.erase(pos1, pos2) removes the part of the string between position 1 and 2.   // In this case, between character 10 and the end of the string.     cout << "Final string: " << text; }

