	if(parent[j] == p) 					parent[j] = parent[edges[i][0]]; 			 			mst[l][0] = edges[i][0]; 			mst[l++][1] = edges[i][1]; 			 			mstVal += edges[i][2]; 		} 	} 	 	for(int i=0;i<l;i++) 		cout<<mst[i][0]<<" -> "<<mst[i][1]<<endl; 	 	cout<<endl; 	cout<<mstVal<<endl; 		 	return(0); }
