MergeSort(arr[], l,  r) If r > l      1. Find the middle point to divide the array into two halves:                middle m = l+ (r-l)/2      2. Call mergeSort for first half:                 Call mergeSort(arr, l, m)      3. Call mergeSort for second half:              Call mergeSort(arr, m+1, r)      4. Merge the two halves sorted in step 2 and 3:              Call merge(arr, l, m, r)
//merge sort #include <iostream>  using namespace std; void merge(int arr[],int start,int mid,int end) {     int n1=mid-start+1;     int n2=end-mid;     int l[n1],m[n2];     for(int i=0;i<n1;i++)     {         l[i]=arr[start+i];     }     for(int j=0;j<n2;j++)     {         m[j]=arr[mid+1+j];     }     int i=0;     int j=0;     int k=start;     while(i<n1&&j<n2)     {         if(l[i]<m[j])         {             arr[k]=l[i];             k++;             i++;         }         else         {             arr[k]=m[j];             k++;             j++;         }     }     while(i<n1)     {         arr[k]=l[i];         k++;         i++;     }     while(j<n2)     {         arr[k]=m[j];         k++;         j++;     } } void mergesort(int arr[],int start,int end) {     if(start<end)     {         int mid=(start+end)/2;         mergesort(arr,start,mid);         mergesort(arr,mid+1,end);         merge(arr,start,mid,end);     } } void display(int arr[],int n) {     for(int i=0;i<n;i++)     {         cout<<arr[i]<<" ";     }     cout<<endl; }  int main() {     int n;     cout<<"enter the size of the array:"<<endl;     cin>>n;     cout<<"enter the elements of the array:"<<endl;     int arr[n];     for(int i=0;i<n;i++)     {         cin>>arr[i];     }     cout<<"array as it is:"<<endl;     display(arr,n);     cout<<"sorted array:"<<endl;     mergesort(arr,0,n-1);     display(arr,n);     return 0; } 
class Sort  {     void merge(int arr[], int left, int middle, int right)     {         int low = middle - left + 1;                    //size of the left subarray         int high = right - middle;                      //size of the right subarray           int L[] = new int[low];                             //create the left and right subarray         int R[] = new int[high];          int i = 0, j = 0;           for (i = 0; i < low; i++)                               //copy elements into left subarray         {             L[i] = arr[left + i];         }         for (j = 0; j < high; j++)                              //copy elements into right subarray         {             R[j] = arr[middle + 1 + j];         }                    int k = left;                                           //get starting index for sort         i = 0;                                             //reset loop variables before performing merge         j = 0;          while (i < low && j < high)                     //merge the left and right subarrays         {             if (L[i] <= R[j])              {                 arr[k] = L[i];                 i++;             }             else              {                 arr[k] = R[j];                 j++;             }             k++;         }           while (i < low)                             //merge the remaining elements from the left subarray         {             arr[k] = L[i];             i++;             k++;         }           while (j < high)                           //merge the remaining elements from right subarray         {             arr[k] = R[j];             j++;             k++;         }     }        void mergeSort(int arr[], int left, int right)       //helper function that creates the sub cases for sorting     {         int middle;         if (left < right) {                             //sort only if the left index is lesser than the right index (meaning that sorting is done)             middle = (left + right) / 2;               mergeSort(arr, left, middle);                    //left subarray             mergeSort(arr, middle + 1, right);               //right subarray               merge(arr, left, middle, right);                //merge the two subarrays         }     }       void display(int arr[])                 //display the array     {           for (int i=0; i<arr.length; ++i)          {             System.out.print(arr[i]+" ");         }      }       public static void main(String args[])     {         int arr[] = { 9, 3, 1, 5, 13, 12 };         Sort ob = new Sort();         ob.mergeSort(arr, 0, arr.length - 1);         ob.display(arr);     } } 
// @see https://www.youtube.com/watch?v=es2T6KY45cA&vl=en // @see https://www.cs.usfca.edu/~galles/visualization/ComparisonSort.html  function merge(list, start, midpoint, end) {     const left = list.slice(start, midpoint);     const right = list.slice(midpoint, end);     for (let topLeft = 0, topRight = 0, i = start; i < end; i += 1) {         if (topLeft >= left.length) {             list[i] = right[topRight++];         } else if (topRight >= right.length) {             list[i] = left[topLeft++];         } else if (left[topLeft] < right[topRight]) {             list[i] = left[topLeft++];         } else {             list[i] = right[topRight++];         }     } }  function mergesort(list, start = 0, end = undefined) {     if (end === undefined) {         end = list.length;     }     if (end - start > 1) {         const midpoint = ((end + start) / 2) >> 0;         mergesort(list, start, midpoint);         mergesort(list, midpoint, end);         merge(list, start, midpoint, end);     }     return list; }  mergesort([4, 7, 2, 6, 4, 1, 8, 3]);
void merge(int arr[], int l, int m, int r)     {          int n=r-l+1,temp[n];          int i=l,j=m+1,k=0;          while(i<=m and j<= r)          {              if(arr[i]< arr[j]){                 temp[k++] = arr[i++];              else                 temp[k++] = arr[j++];          }          while(i<=m)             temp[k++] = arr[i++];          while(j<=r)             temp[k++] = arr[j++];          int ind= 0;          for(int i=l;i<=m;i++)              arr[i] = temp[ind++];          for(int j=m+1;j<=r;j++)             arr[j] = temp[ind++];     }          void mergeSort(int arr[], int l, int r)     {        if(l<r)        {            int mid = (r+l)/2;            mergeSort(arr,l,mid);            mergeSort(arr,mid+1,r);            merge(arr,l,mid,r);        }     }
# Python3 recursive merge sort algorithm -> O(n*log(n)) def merge_sort(A):     def merge(l, r):         i = j = 0         n = []  # merging container         while i < len(l) or j < len(r):              # if no more elements to the right,             # add remaining left elements             if i == len(l):                 n.extend(r[j:])                 break              # if no more elements to the left,             # add remaining right elements             if j == len(r):                 n.extend(l[i:])                 break              # if elements left on both sides,             # add smaller element             a, b = l[i], r[j]             if a < b:                 n.append(a)                 i += 1             else:                 n.append(b)                 j += 1          return n      # divide list down to single-elements     s = len(A)     if s > 1:         s //= 2         l = merge_sort(A[:s])  # split left         r = merge_sort(A[s:])  # split right         return merge(l, r)  # merge sides in order     else:         return A
