// Minimum spanning tree using Prim's Algorithm Efficient Approach //     Using Priority Queue // Watch striver graph series :) #include<bits/stdc++.h> using namespace std; void addedge(vector<pair<int,int>>adj[],int u,int v,int weight) {     adj[u].push_back(make_pair(v,weight));     adj[v].push_back(make_pair(u,weight)); } int main() {     int vertex,edges;     cout<<"ENTER THE NUMBER OF VERTEX AND EDGES:"<<endl;     cin>>vertex>>edges;     vector<pair<int,int>>adj[vertex];     int a,b,w;     cout<<"ENTER THE LINKS:"<<endl;     for(int i=0;i<edges;i++)     {         cin>>a>>b>>w;         addedge(adj,a,b,w);     }     int parent[vertex],key[vertex];     bool mset[vertex];     for(int i=0;i<vertex;i++)     {         parent[i]=-1;         key[i]=INT_MAX;         mset[i]=false;     }     priority_queue<pair<int,int>,vector<pair<int,int>>,greater<pair<int,int>>>pq;     parent[0]=-1;     key[0]=0;     pq.push(make_pair(0,0));//storing Key[i] and i     for(int count=0;count<vertex-1;count++)     {         int u=pq.top().second;         pq.pop();         mset[u]=true;         for(auto it:adj[u])         {             int v=it.first;             int weight=it.second;             if(mset[v]==false&&weight<key[v])             {                 parent[v]=u;                 pq.push(make_pair(key[v],v));                 key[v]=weight;             }         }     }     for(int i=1;i<vertex;i++)     {         cout<<parent[i]<<"->"<<i<<endl;     }     return 0; }

