/*  * Program  : Queue using linked list  * Language : C  */  #include<stdio.h> #include<stdlib.h>  struct node {     int data;     struct node *next; };  struct node *front = NULL, *rear = NULL;  void enqueue(int val) {     struct node *newNode = malloc(sizeof(struct node));     newNode->data = val;     newNode->next = NULL;      //if it is the first node     if(front == NULL && rear == NULL)         //make both front and rear points to the new node         front = rear = newNode;     else     {         //add newnode in rear->next         rear->next = newNode;          //make the new node as the rear node         rear = newNode;     } }  void dequeue() {     //used to free the first node after dequeue     struct node *temp;      if(front == NULL)          printf("Queue is Empty. Unable to perform dequeue
");     else     {         //take backup         temp = front;          //make the front node points to the next node         //logically removing the front element         front = front->next;          //if front == NULL, set rear = NULL         if(front == NULL)             rear = NULL;         //free the first node        free(temp);     }  }  void printList() {     struct node *temp = front;      while(temp)     {         printf("%d->",temp->data);         temp = temp->next;     }     printf("NULL
"); }  int main() {     enqueue(10);     enqueue(20);     enqueue(30);     printf("Queue :");     printList();     dequeue();     printf("After dequeue the new Queue :");     printList();     dequeue();     printf("After dequeue the new Queue :");     printList();      return 0; } 
