//Code by Soumyadeep //insta id: @soumyadepp  #include <bits/stdc++.h> #define ll long long  using namespace std;  int maxElement(vector<int> arr) {     int m = arr[0];     for (int i = 0; i < arr.size(); i++)     {         if (arr[i] > m)             m = arr[i];     }     return m; }  int digitCnt(int x) {     int z = x;     int c = 1;     while (z)     {         z /= 10;         c++;     }     return c; } void radixSort(vector<int> &arr) {     int x = maxElement(arr);               //O(N)     int countDigits = digitCnt(x);         //O(numberofdigitsinx)     for (int i = 1; i <= countDigits; i++) //runs number of digits in maximum element in the array     {         vector<int> holder[10];         for (int j = 0; j < arr.size(); j++) //O(N)         {             int m = i;             int k;             int p = arr[j];             while (m) //O(logN)             {                 k = p % 10;                 p /= 10;                 m--;             }             holder[k].push_back(arr[j]);         }         arr.clear();         for (int c = 0; c < 10; c++)         {             arr.insert(arr.end(), holder[c].begin(), holder[c].end());         }     }     //approx O(ND) } int main() {     int t;     cin >> t;     while (t--)     {         vector<int> arr;         int n, x;         cin >> n;         for (int i = 0; i < n; i++)         {             cin >> x;             arr.push_back(x);         }         radixSort(arr);         cout << "The sorted array is " << endl;          for (int i = 0; i < arr.size(); i++)             cout << arr[i] << " ";         cout << endl;     }     return 0; }
 import java.io.*;  import java.util.*;  class Radix {             static int getMax(int arr[], int n){             int mx = arr[0];             for (int i = 1; i < n; i++)                   if (arr[i] > mx)                          mx = arr[i];             return mx;      }     static void countSort(int arr[], int n, int exp)      {               int output[] = new int[n];             int i;             int count[] = new int[10];             Arrays.fill(count,0);            for (i = 0; i < n; i++)                     count[ (arr[i]/exp) ]++;             // Change count[i] so that count[i] now contains             // actual position of this digit in output[]             for (i = 1; i < 10; i++)                     count[i] += count[i - 1];             // Build the output array             for (i = n - 1; i >= 0; i--){                    output[count[ (arr[i]/exp) ] - 1] = arr[i];                     count[ (arr[i]/exp) ]--;          }            for (i = 0; i < n; i++)                     arr[i] = output[i];      }     static void radixsort(int arr[], int n)         { // Find the maximum number to know number of digits             int m = getMax(arr, n);            for (int exp = 1; m/exp > 0; exp *= 10)                    countSort(arr, n, exp);      }     static void print(int arr[], int n)      {          for (int i=0; i<n; i++)                 System.out.print(arr[i]+" ");      }      public static void main (String[] args)      {    int arr[] = {170, 45, 75, 90, 802, 24, 2, 66};           int n = arr.length;           radixsort(arr, n);           print(arr, n);      }  }  JavaCopy

