 to update a value in input array and segment tree.  // It uses updateValueUtil() to update the value in segment tree  void updateValue(int arr[], int *st, int n, int i, int new_val)  {  	// Check for erroneous input index  	if (i < 0 || i > n-1)  	{  		cout<<"Invalid Input";  		return;  	}   	// Get the difference between new value and old value  	int diff = new_val - arr[i];   	// Update the value in array  	arr[i] = new_val;   	// Update the values of nodes in segment tree  	updateValueUtil(st, 0, n-1, i, diff, 0);  }   // Return sum of elements in range from index qs (quey start)  // to qe (query end). It mainly uses getSumUtil()  int getSum(int *st, int n, int qs, int qe)  {  	// Check for erroneous input values  	if (qs < 0 || qe > n-1 || qs > qe)  	{  		cout<<"Invalid Input";  		return -1;  	}   	return getSumUtil(st, 0, n-1, qs, qe, 0);  }   // A recursive function that constructs Segment Tree for array[ss..se].  // si is index of current node in segment tree st  int constructSTUtil(int arr[], int ss, int se, int *st, int si)  {  	// If there is one element in array, store it in current node of  	// segment tree and return  	if (ss == se)  	{  		st[si] = arr[ss];  		return arr[ss];  	}   	// If there are more than one elements, then recur for left and  	// right subtrees and store the sum of values in this node  	int mid = getMid(ss, se);  	st[si] = constructSTUtil(arr, ss, mid, st, si*2+1) +  			constructSTUtil(arr, mid+1, se, st, si*2+2);  	return st[si];  }   /* Function to construct segment tree from given array. This function  allocates memory for segment tree and calls constructSTUtil() to  fill the allocated memory */ int *constructST(int arr[], int n)  {  	// Allocate memory for the segment tree   	//Height of segment tree  	int x = (int)(ceil(log2(n)));   	//Maximum size of segment tree  	int max_size = 2*(int)pow(2, x) - 1;   	// Allocate memory  	int *st = new int[max_size];   	// Fill the allocated memory st  	constructSTUtil(arr, 0, n-1, st, 0);   	// Return the constructed segment tree  	return st;  }   // Driver program to test above functions  int main()  {  	int arr[] = {1, 3, 5, 7, 9, 11};  	int n = sizeof(arr)/sizeof(arr[0]);   	// Build segment tree from given array  	int *st = constructST(arr, n);   	// Print sum of values in array from index 1 to 3  	cout<<"Sum of values in given range = "<<getSum(st, n, 1, 3)<<endl;   	// Update: set arr[1] = 10 and update corresponding  	// segment tree nodes  	updateValue(arr, st, n, 1, 10);   	// Find sum after the value is updated  	cout<<"Updated sum of values in given range = " 			<<getSum(st, n, 1, 3)<<endl;  	return 0;  }  //This code is contributed by rathbhupendra  
void build(int node, int start, int end) {     if(start == end)     {         // Leaf node will have a single element         tree[node] = A[start];     }     else     {         int mid = (start + end) / 2;         // Recurse on the left child         build(2*node, start, mid);         // Recurse on the right child         build(2*node+1, mid+1, end);         // Internal node will have the sum of both of its children         tree[node] = tree[2*node] + tree[2*node+1];     } }
class SegmentTree{ public:     vector<int> segv;     int n;     SegmentTree(vector<int> &nums) {         if (!nums.size()) return ;         segv.assign(nums.size() * 4, 0);         n = nums.size();         build(nums, 1, 0, n - 1);      }     void build(vector<int> &nums, int v, int l, int r) {         if (l == r) segv[v] = nums[l];         else {             int mid = l + (r - l) / 2;             build(nums, v * 2, l, mid);             build(nums, v * 2 + 1, mid + 1, r);             segv[v] = segv[v * 2] + segv[v * 2 + 1];          }     }     int sumRange(int v, int l, int r, int a, int b) {         if (a > b) return 0;         if (l == a && r == b) return segv[v];         int mid = l + (r - l) / 2;         return sumRange(v * 2, l, mid, a, min(mid, b))             + sumRange(v * 2 + 1, mid + 1, r, max(mid + 1, a), b);     }     void update(int v, int l, int r, int pos, int val) {         if (l == r) segv[v] = val;         else {             int mid = l + (r - l) / 2;             if (pos <= mid) update(v * 2, l, mid, pos, val);             else update(v * 2 + 1, mid + 1, r, pos, val);             segv[v] = segv[v * 2] + segv[v * 2 + 1];         }     }     int sumRange(int a, int b) {         return sumRange(1, 0, n - 1, a, b);     }     void update(int pos, int val) {         update(1, 0, n - 1, pos, val);     } };

