// C algorithm for SelectionSort  void selectionSort(int arr[], int n) { 	for(int i = 0; i < n-1; i++) 	{ 		int min = i;          		for(int j = i+1; j < n; j++) 		{ 			if(arr[j] < arr[min])             	min = j; 		}          		if(min != i) 		{         	// Swap 			int temp = arr[i]; 			arr[i] = arr[min]; 			arr[min] = temp; 		} 	} }
#include <bits/stdc++.h>  using namespace std;   void selectionSort(int arr[], int n){     int i,j,min;          for(i=0;i<n-1;i++){         min = i;         for(j=i+1;j<n;j++){             if(arr[j] < arr[min]){                 min = j;             }         }         if(min != i){             swap(arr[i],arr[min]);         }     } }  int main()   {       int arr[] = { 1,4,2,5,333,3,5,7777,4,4,3,22,1,4,3,666,4,6,8,999,4,3,5,32 };       int n = sizeof(arr) / sizeof(arr[0]);        selectionSort(arr, n);        for(int i = 0; i < n; i++){         cout << arr[i] << " ";     }      return 0;   }  
SelectionSort(List) {   for(i from 0 to List.Length) {     SmallestElement = List[i]     for(j from i to List.Length) {       if(SmallestElement > List[j]) {         SmallestElement = List[j]       }     }     Swap(List[i], SmallestElement)   } }
//I Love Java import java.util.*; import java.io.*; import java.util.stream.*; import static java.util.Collections.*; import static java.util.stream.Collectors.*;  public class Selection_Sort_P {     public static void main(String[] args) throws IOException {         BufferedReader buffer = new BufferedReader(new InputStreamReader(System.in));         List<Integer> arr = Stream.of(buffer.readLine().replaceAll(("\\s+$"), "").split(" ")).map(Integer::parseInt)                 .collect(toList());          int high = arr.size();         selection_sort(arr, high);          System.out.println(arr);     }      public static void swap(List<Integer> arr, int i, int j) {         int temp = arr.get(i);         arr.set(i, arr.get(j));         arr.set(j, temp);     }      public static void selection_sort(List<Integer> arr, int high) {         for (int i = 0; i <= high - 1; i++) {             steps(arr, i, high);         }     }      public static void steps(List<Integer> arr, int start, int high) {         for (int i = start; i <= high - 1; i++) {             if (arr.get(i) < arr.get(start)) {                 swap(arr, start, i);             }         }     } } 
class Sort  {      void selectionSort(int arr[])      {          int pos;         int temp;         for (int i = 0; i < arr.length; i++)          {              pos = i;              for (int j = i+1; j < arr.length; j++)             {                 if (arr[j] < arr[pos])                  //find the index of the minimum element                 {                     pos = j;                 }             }              temp = arr[pos];            //swap the current element with the minimum element             arr[pos] = arr[i];              arr[i] = temp;          }      }         void display(int arr[])                     //display the array     {          for (int i=0; i<arr.length; i++)          {             System.out.print(arr[i]+" ");         }       }         public static void main(String args[])      {          Sort ob = new Sort();          int arr[] = {64,25,12,22,11};          ob.selectionSort(arr);          ob.display(arr);      }  }  
