//selection sort; timecomplexity=O(n^2);space complexity=O(n);auxiliary space complexity=O(1) #include <iostream>  using namespace std; void swap(int*,int*); void selection_sort(int arr[],int n) {     for(int i=0;i<n-1;i++)     {         for(int j=i+1;j<n;j++)         {             if(arr[i]>arr[j])             {                 swap(&arr[i],&arr[j]);             }         }     } } void display(int arr[],int n) {     for(int i=0;i<n;i++)     {         cout<<arr[i]<<" ";     }     cout<<endl; }  int main() {     int n;     cout<<"enter the size of the array:"<<endl;     cin>>n;     int array_of_numbers[n];     cout<<"enter the elements of the array:"<<endl;     for(int i=0;i<n;i++)     {         cin>>array_of_numbers[i];     }     cout<<"array as it was entered"<<endl;     display(array_of_numbers,n);     cout<<"array after sorting:"<<endl;     selection_sort(array_of_numbers,n);     display(array_of_numbers,n);     return 0; } void swap(int *a,int *b) {     int temp=*a;     *a=*b;     *b=temp; } 
