     void sort(int *arr, int n){                  // Incrementa di 1 il limite inferiore del sub array da ordinare         for (int i = 0; i < n-1; i++)          {              // Trova il minimo nel subarray da ordinare             int indice_min = i;              for (int j = i+1; j < n; j++) {                                  // Confronto per trovare un nuovo minimo                 if (arr[j] < arr[indice_min])                      indice_min = j; // Salvo l'indice del nuovo minimo             }                          // Scambia il minimo trovato con il primo elemento             swap(arr,indice_min,i);             }      }           void swap(int *arr, int a , int b){         int temp = arr[a];         arr[a] = arr[b];         arr[b] = temp;     }      }

