class Solution { public:     int minStoneSum(vector<int>& piles, int k) {         int sum=accumulate(piles.begin(), piles.end(), 0);         while(k!=0){             int i=*max_element(piles.begin(),piles.end());             std::vector<int>::iterator it = std::find(piles.begin(), piles.end(), i);             int index = std::distance(piles.begin(), it);             int t=floor(piles[index]/2);              piles[index]=i-t;             sum=sum-t;             k--;         }                 return sum;     }      };
