val x = 3 when(x) {     3 -> println("yes")     8 -> println("no")     else -> println("maybe") } // when can also be used as an expression! val y = when(x) {     3 -> "yes"     8 -> "no"     else -> "maybe" } println(y) // "yes"
when (x) {     1 -> print("x == 1")     2 -> print("x == 2")     else -> { // Note the block         print("x is neither 1 nor 2")     } }
fun numberTypeName(x: Number) = when(x) {  0 -> "Zero" // Equality check  in 1..4 -> "Four or less" // Range check  5, 6, 7 -> "Five to seven" // Multiple values  is Byte -> "Byte" // Type check  else -> "Some number" }
fun signAsString(x: Int)= when {  x < 0 -> "Negative"  x == 0 -> "Zero"  else -> "Positive" }
