// how to sort an array in java without using sort() method (ascending order) public class WithoutSortMethod {    public static void main(String[] args)    {       int temp;       int[] arrNumbers = {14, 8, 5, 54, 41, 10, 1, 500};       System.out.println("Before sort: ");       for(int num : arrNumbers)       {          System.out.println(num);       }       for(int a = 0; a < arrNumbers.length; a++)       {          for(int b = a + 1; b < arrNumbers.length; b++)          {             if(arrNumbers[a] > arrNumbers[b])             {                temp = arrNumbers[a];                arrNumbers[a] = arrNumbers[b];                arrNumbers[b] = temp;             }          }       }       System.out.println("---------------");       System.out.println("After sort: ");       for(int num : arrNumbers)       {          System.out.println(num);       }    } }
