Convert decimal to binary using stack in java import java.util.*; public class DecimalBinaryExample {    public static void main(String[] args)    {       Scanner sc = new Scanner(System.in);                       Stack<Integer> numStack = new Stack<Integer>();            System.out.println("Please enter a decimal number : ");       int number = sc.nextInt();       while(number != 0)       {          int a = number % 2;          numStack.push(a);          number /= 2;       }       System.out.println("Binary number : ");       while(!(numStack.isEmpty()))       {          System.out.print(numStack.pop());       }       System.out.println();       sc.close();    } }

