// Prime number program in java using scanner import java.util.Scanner; public class PrimeNumberUsingScanner  {    public static void main(String[] args)     {       Scanner sc = new Scanner(System.in);         System.out.println("Please enter a number to check prime number: ");         int number = sc.nextInt();         if(checkPrimeNumber(number))        {            System.out.println(number + " is a prime number");         }       else        {            System.out.println(number + " is not a prime number");         }       sc.close();    }    public static boolean checkPrimeNumber(int number)     {         if(number <= 1)        {            return false;       }         for(int a = 2; a < Math.sqrt(number); a++)        {            if(number % a == 0)           {               return false;            }         }         return true;      } }
