package com.javaaid.hackerrank.solutions.tutorials.ctci;  import java.util.ArrayList; import java.util.LinkedList; import java.util.Queue; import java.util.Scanner; import java.util.Stack;  class Graph {    private final int V;   private int E;   private ArrayList<Integer>[] adj;    Graph(int V) {     adj = (ArrayList<Integer>[]) new ArrayList[V + 1];     this.V = V;     this.E = 0;     for (int v = 1; v <= V; v++) adj[v] = new ArrayList<Integer>(V);   }    Graph(Scanner in) {     this(in.nextInt());     int E = in.nextInt();     for (int i = 0; i < E; i++) {       int v = in.nextInt();       int w = in.nextInt();       addEdge(v, w);     }   }    public int V() {     return V;   }    public int E() {     return E;   }    public void addEdge(int v, int w) {     adj[v].add(w);     adj[w].add(v);     E++;   }    public Iterable<Integer> adj(int v) {     return adj[v];   } }  class BreadthFirstPaths {    private int s;   private boolean marked[];   private int edgeTo[];    BreadthFirstPaths(Graph G, int s) {     marked = new boolean[G.V() + 1];     this.s = s;     edgeTo = new int[G.V() + 1];     bfs(G, s);   }    private void bfs(Graph G, int s) {     Queue<Integer> q = (Queue<Integer>) new LinkedList<Integer>();     q.add(s);     while (!q.isEmpty()) {       int v = q.poll();       marked[v] = true;       for (int w : G.adj(v)) {         if (!marked[w]) {           marked[w] = true;           edgeTo[w] = v;           q.add(w);         }       }     }   }    public Iterable<Integer> pathTo(int v) {     if (!hasPathTo(v)) return null;     Stack<Integer> path = new Stack<Integer>();     for (int x = v; x != s; x = edgeTo[x]) path.push(x);     path.push(s);     return path;   }    public boolean hasPathTo(int v) {     return marked[v];   } }  public class BFSShortestReachInAGraph {    public static void main(String[] args) {     Scanner sc = new Scanner(System.in);     int q = sc.nextInt();     for (int i = 0; i < q; i++) {       Graph G = new Graph(sc);       int s = sc.nextInt();       BreadthFirstPaths bfp = new BreadthFirstPaths(G, s);       for (int v = 1; v <= G.V(); v++) {         if (s != v) {           if (bfp.hasPathTo(v)) {             Stack<Integer> st = (Stack<Integer>) bfp.pathTo(v);             int sum = 0;             for (int x = 1; x < st.size(); x++) {               sum += 6;             }             System.out.print(sum + " ");           } else {             System.out.print(-1 + " ");           }         }       }       System.out.println();       sc.close();     }   } } 
#include<iostream> #include <list>   using namespace std;     class Graph {     int V;             list<int> *adj;    public:     Graph(int V);              void addEdge(int v, int w);             void BFS(int s);   };   Graph::Graph(int V) {     this->V = V;     adj = new list<int>[V]; }   void Graph::addEdge(int v, int w) {     adj[v].push_back(w);  }   void Graph::BFS(int s) {        bool *visited = new bool[V];     for(int i = 0; i < V; i++)         visited[i] = false;           list<int> queue;           visited[s] = true;     queue.push_back(s);           list<int>::iterator i;       while(!queue.empty())     {                 s = queue.front();         cout << s << " ";         queue.pop_front();                  for (i = adj[s].begin(); i != adj[s].end(); ++i)         {             if (!visited[*i])             {                 visited[*i] = true;                 queue.push_back(*i);             }         }     } }    int main() {          Graph g(4);     g.addEdge(0, 1);     g.addEdge(0, 2);     g.addEdge(1, 2);     g.addEdge(2, 0);     g.addEdge(2, 3);     g.addEdge(3, 3);       cout << "Following is Breadth First Traversal "          << "(starting from vertex 2) \n";     g.BFS(2);       return 0; }

// Java implementation of iterative Binary Search  class BinarySearch {  	// Returns index of x if it is present in arr[],  	// else return -1  	int binarySearch(int arr[], int x)  	{  		int l = 0, r = arr.length - 1;  		while (l <= r) {  			int m = l + (r - l) / 2;   			// Check if x is present at mid  			if (arr[m] == x)  				return m;   			// If x greater, ignore left half  			if (arr[m] < x)  				l = m + 1;   			// If x is smaller, ignore right half  			else 				r = m - 1;  		}   		// if we reach here, then element was  		// not present  		return -1;  	}   	// Driver method to test above  	public static void main(String args[])  	{  		BinarySearch ob = new BinarySearch();  		int arr[] = { 2, 3, 4, 10, 40 };  		int n = arr.length;  		int x = 10;  		int result = ob.binarySearch(arr, x);  		if (result == -1)  			System.out.println("Element not present");  		else 			System.out.println("Element found at " 							+ "index " + result);  	}  } 
public class Main{ public static void binarySearch(int arr[], int a, int b, int key){ int mid = (a + b)/2; while( a <= b ){ if ( arr[mid] < key ){ a = mid + 1; }else if ( arr[mid] == key ){ System.out.println("number is here " + mid); break; }else{ b = mid - 1; } mid = (a + b)/2; } if ( a > b ){ System.out.println("number not here"); } } public static void main(String args[]){ int arr[] = {10,20,30,40,50}; int key = 30; int b=arr.length-1; binarySearch(arr,0,b,key); } } 
public int runBinarySearchRecursively(   int[] sortedArray, int key, int low, int high) {     int middle = (low + high) / 2;              if (high < low) {         return -1;     }      if (key == sortedArray[middle]) {         return middle;     } else if (key < sortedArray[middle]) {         return runBinarySearchRecursively(           sortedArray, key, low, middle - 1);     } else {         return runBinarySearchRecursively(           sortedArray, key, middle + 1, high);     } } 
binary search program in java. public class BinarySearchExample {    public static void binarySearch(int[] arrNumbers, int start, int end, int keyElement)    {       int middle = (start + end) / 2;       while(start <= end)       {          if(arrNumbers[middle] < keyElement)          {             start = middle + 1;          }          else if(arrNumbers[middle] == keyElement)          {             System.out.println("Element found at index: " + middle);             break;          }          else          {             end = middle - 1;          }          middle = (start + end) / 2;       }       if(start > end)       {          System.out.println("Element not found!");       }    }    public static void main(String[] args)    {       int[] arrNumbers = {14,15,16,17,18};       int keyElement = 16;       int end = arrNumbers.length - 1;       binarySearch(arrNumbers, 0, end, keyElement);    } }
