// Find minimum and maximum values in a java array public class MaxMinValueInArray {    public static void main(String[] args)     {       int[] arrNumber = new int[]{50, 150, 250, 500, 160, 530};       int maximum = maxNumber(arrNumber);       System.out.println("Maximum value : " + maximum);       // calling min function java       int minimum = minNumber(arrNumber);       System.out.println("Minimum value : " + minimum);    }    // java find max in array    public static int maxNumber(int[] arr)    {       int maximumValue = arr[0];       for(int a = 1; a < arr.length; a++)       {          if(arr[a] > maximumValue)          {             maximumValue = arr[a];          }       }       return maximumValue;    }    // java min of array    public static int minNumber(int[] arr)    {       int minimumValue = arr[0];       // find minimum value in array java       for(int a = 1; a < arr.length; a++)       {          if(arr[a] < minimumValue)          {             minimumValue = arr[a];          }       }       return minimumValue;    } }
