package myForLoops  public class forLoop { 	public static void main(String[] args) {      		for (int i = j; i<=k; i++)           //variable "i" is the value checked in loop         //variable j is the starting value for i         //variable k is where we want the loop to end         //i++ adds 1 to i each iteration of the loop until i == k 		{         	//displays "i" 			System.out.println(i); 		}                  //this is a for loop in use         for (int i = 1; i<=10; i++) {         	System.out.println(i); //i will be displayed 10 times being              //increased by 1 every time         }                  for (char ch = 'k'; ch<= 'q'; ch++) {         	//this is a for loop with characters, it will             //display every character from 'k' to 'q'         	System.out.println(ch);         }         for (int i = 10; i>=1; i++) {         	//this loop will be infinitely run since i              //will always be greater than 1, we do not want this         	System.out.println(i);         }         for (int i = 10; i>=1; i--) {          	//this is the correct way of doing the previous code              //(if you do not want an infinite for loop)         	System.out.println(i);         } 	} }
