public class BubbleSortExample {       static void bubbleSort(int[] arr) {           int n = arr.length;           int temp = 0;            for(int i=0; i < n; i++){                    for(int j=1; j < (n-i); j++){                             if(arr[j-1] > arr[j]){                                    //swap elements                                    temp = arr[j-1];                                    arr[j-1] = arr[j];                                    arr[j] = temp;                            }                                               }            }      }  
public static void bubbleSort(int arr[]) { 	for (int i = 0; i < arr.length; i++) //number of passes     { 		//keeps track of positions per pass           	for (int j = 0; j < (arr.length - 1 - i); j++) //Think you can add a -i to remove uneeded comparisons          {           	//if left value is great than right value          	if (arr[j] > arr[j + 1])             {               	//swap values             	int temp = arr[j];               	arr[j] = arr[j + 1];               	arr[j + 1] = temp;              }         }     } }
public class DemoBubbleSort {    void bubbleSort(int[] arrNum)    {       int num = arrNum.length;       for(int a = 0; a < num - 1; a++)       {          for(int b = 0; b < num - a - 1; b++)          {             if(arrNum[b] > arrNum[b + 1])             {                int temp = arrNum[b];                arrNum[b] = arrNum[b + 1];                arrNum[b + 1] = temp;             }          }       }    }    void printingArray(int[] arrNum)    {       int number = arrNum.length;       for(int a = 0; a < number; ++a)       {          System.out.print(arrNum[a] + " ");          System.out.println();       }    }    public static void main(String[] args)    {       DemoBubbleSort bs = new DemoBubbleSort();       int[] arrSort = {65, 35, 25, 15, 23, 14, 95};       bs.bubbleSort(arrSort);       System.out.println("After sorting array: ");       bs.printingArray(arrSort);    } }
func Sort(arr []int) []int { 	for i := 0; i < len(arr)-1; i++ { 		for j := 0; j < len(arr)-i-1; j++ { 			if arr[j] > arr[j+1] { 				temp := arr[j] 				arr[j] = arr[j+1] 				arr[j+1] = temp 			} 		} 	} 	return arr }

