public class Pathnames {      public static void main(String[] args) {         // Creates an array in which we will store the names of files and directories         String[] pathnames;          // Creates a new File instance by converting the given pathname string         // into an abstract pathname         File f = new File("D:/Programming");          // Populates the array with names of files and directories         pathnames = f.list();          // For each pathname in the pathnames array         for (String pathname : pathnames) {             // Print the names of files and directories             System.out.println(pathname);         }     } } 
	public static List<String> mapFolder(String path, boolean includeEmptyFolders) {     	List<String> map = new ArrayList<String>();     	List<String> unmappedDirs = new ArrayList<String>();     	File[] items = new File(path).listFiles();      	if (!path.substring(path.length() - 1).equals("/")) {     		path += "/";     	}     		     	if (items != null) { 	    	for (File item : items) { 	    		if (item.isFile()) 	    				map.add(path+item.getName()); 	    		else 	    			unmappedDirs.add(path+item.getName()); 	    	} 	    	 	    	if (!unmappedDirs.isEmpty()) { 	    		for (String folder : unmappedDirs) { 	    			List<String> temp = mapFolder(folder, includeEmptyFolders); 	    			if (!temp.isEmpty()) { 	    				for (String item : temp) 	    					map.add(item);     				} else if (includeEmptyFolders == true)     					map.add(folder+"/"); 	    		} 	    	}     	}     	return map;     }

