public static double max(double[] arr) {   double result = arr[0];   for (int i = 1; i < arr.length; ++i) {     if (result < arr[i]) result = arr[i];   }   return result; }
public static String max(String[] arr) {   String result = arr[0];   for (int i = 1; i < arr.length; ++i) {     if (result.compareTo(arr[i]) < 0) result = arr[i];   }   return result; }
public static double arrayMax(double[] arr) {     double max = Double.NEGATIVE_INFINITY;      for(double cur: arr)         max = Math.max(max, cur);      return max; }
public static double max(double[] arr) {   double result = Double.NEGATIVE_INFINITY;   for (int i = 0; i < arr.length; ++i) {     if (result < arr[i]) result = arr[i];   }   return result; }
