public class PrimeNumberInJava {    public static void main(String[] args)    {       int a, b = 0, flag = 0;       int number = 17;       b = number / 2;       if(number == 0 || number == 1)       {          System.out.println(number + " is not prime number");       }       else       {          for(a = 2; a <= b; a++)          {             if(number % a == 0)             {                System.out.println(number + " is not prime number.");                flag = 1;                break;             }          }          if(flag == 0)          {             System.out.println(number + " is prime number.");          }       }    } }
