// Factorial program using recursion public class FactorialRecursion {    public static void main(String[] args)    {       int factorial = 1;       int number = 6;       factorial = factorialFunction(number);       System.out.println("Factorial of " + number + " is : " + factorial);    }    static int factorialFunction(int num)    {       if(num == 0)       {          return 1;       }       else       {          return(num * factorialFunction(num - 1));       }    } }
