// how to sort an array in java without using sort() method (ascending order) public class WithoutSortMethod {    public static void main(String[] args)    {       int temp;       int[] arrNumbers = {14, 8, 5, 54, 41, 10, 1, 500};       System.out.println("Before sort: ");       for(int num : arrNumbers)       {          System.out.println(num);       }       for(int a = 0; a < arrNumbers.length; a++)       {          for(int b = a + 1; b < arrNumbers.length; b++)          {             if(arrNumbers[a] > arrNumbers[b])             {                temp = arrNumbers[a];                arrNumbers[a] = arrNumbers[b];                arrNumbers[b] = temp;             }          }       }       System.out.println("---------------");       System.out.println("After sort: ");       for(int num : arrNumbers)       {          System.out.println(num);       }    } }
// how to sort an array in java without using sort method public class WithoutUsingSortMethod {    public static void main(String[] args)     {       int temp;       int[] arrNumbers = {14, 8, 5, 54, 41, 10, 1, 500};       System.out.println("Before sort: ");       for(int num : arrNumbers)       {          System.out.println(num);       }       for(int a = 0; a <= arrNumbers.length - 1; a++)       {          for(int b = 0; b <= arrNumbers.length - 2; b++)          {             if(arrNumbers[b] < arrNumbers[b + 1])             {                temp = arrNumbers[b];                arrNumbers[b] = arrNumbers[b + 1];                arrNumbers[b + 1] = temp;             }          }       }       System.out.println("---------------");       System.out.println("After sort: ");       for(int num : arrNumbers)         {          System.out.println(num);       }    } }
// how to sort an array of strings in descending import java.util.Arrays; import java.util.Collections; public class SortStringAlphabeticalOrder  {    public static void main(String[] args)     {       String[] strDesc = {"Bear","Fox","Deer","Cheetah","Anteater","Elephant"};       System.out.println("Before sorting: ");       for(String string : strDesc)       {          System.out.println(string);       }       // arrays.sort       Arrays.sort(strDesc, Collections.reverseOrder());          System.out.println("-----------------------------------");       System.out.println("Sorting string array in descending or reverse order: ");       for(String string : strDesc)       {          System.out.println(string);       }    } }
