/**  * Shuffles array in place.  * @param {Array} a items An array containing the items.  */ function shuffle(a) {     var j, x, i;     for (i = a.length - 1; i > 0; i--) {         j = round(random() * (i + 1));         x = a[i];         a[i] = a[j];         a[j] = x;     }     return a; }  shuffle(array);
function randomArrayShuffle(array) {    var currentIndex = array.length, temporaryValue, randomIndex;    while (0 !== currentIndex) {      randomIndex = Math.floor(Math.random() * currentIndex);      currentIndex -= 1;      temporaryValue = array[currentIndex];      array[currentIndex] = array[randomIndex];      array[randomIndex] = temporaryValue;    }    return array;  }  var alphabet=["a","b","c","d","e"];  randomArrayShuffle(alphabet);   //alphabet is now shuffled randomly = ["d", "c", "b", "e", "a"]      
function shuffleArray(array) {     for (let i = array.length - 1; i > 0; i--) {         const j = Math.floor(Math.random() * (i + 1));         [array[i], array[j]] = [array[j], array[i]];     } }
