const createDB = require('./.');

console.log({ createDB })
const db = createDB("db", '.txt');
console.log(db);

db.put('level1', {
    name: "Level One", map: [
        0, 0, 0, 0,
        0, 1, 2, 0,
        1, 1, 1, 1
    ],
    attachment: "This goes in a different file",
}, (err) => {
    if (err) throw err;
    console.log("level1 saved.")

    db.get('level1', (err, data) => {
        if (err) throw err;
        console.log('level 1 loaded', data);
    });
});